import { Container, Grid, Typography } from '@mui/material';
import React from 'react'
import { ProductAddToCart, ProductImage } from '../../styles/products';
import { products } from '../../data';
import { Matches } from '../../styles/theme';
import PageBanner from '../banner/PageBanner';

const ProductDetail = () => {
    const matches= Matches();
    console.log(products[0].images);
    
  return (
    <>
    <Container>
        <PageBanner></PageBanner>
        <Grid container >
            <Grid item md={6} sx={{textAlign:"center"}}> 
                <ProductImage src={products[0].images} sx={{
                    maxWidth:"inherit"
                }}/>
            </Grid>
            <Grid item md={6}>
                <Typography  variant={matches ? "h6" : "h5"} lineHeight={2}>
                    {products[0].name}
                </Typography>
            <Typography variant={matches ? "caption" : "body1"}>
               <b>Price:</b> ${products[0].price}
            </Typography>
            <Typography variant={ "body1"}>
                <b>Description:</b> {products[0].description}
            </Typography>
             <ProductAddToCart variant="contained">Add to cart</ProductAddToCart>
            </Grid>
        </Grid>
    </Container>
    </>
  )
}

export default ProductDetail;
