// import { products } from "../../data";
// import { Product, ProductImage } from "../../styles/products";

// import { Box, CardMedia, Typography } from "@mui/material";
import { Stack } from "@mui/material";
import { Product, ProductActionsWrapper, ProductFavButton, ProductImage ,ProductAddToCart,ProductActionButton } from "../../styles/products";
import ProductMeta from "./ProductMeta";
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';
import FitScreenIcon from '@mui/icons-material/FitScreen';
import { useState } from "react";


export default function SingleProductsDesktop({product, matches}){

    const [showOptaions, setShowOptions] = useState(false);

    const handleMouseEnter = () =>{
        setShowOptions(true);
    }
    const handleMouseLeave = () => {
        setShowOptions(false);
    }

    return(
        <>
        <Product onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
            <ProductImage src={product.images}/>
            <ProductFavButton isFav={0}>
            <FavoriteIcon/>
            </ProductFavButton>
            
                {showOptaions && <ProductAddToCart show={showOptaions} variant="outline">Add to cart</ProductAddToCart>}
            
            <ProductMeta product={product} matches={matches}/>
           <ProductActionsWrapper>
            <Stack direction="row">
                <ProductFavButton isFav={0}>
                    <FavoriteIcon/>
                </ProductFavButton>
                <ProductActionButton>
                    <ShareIcon color="primary"/>
                </ProductActionButton>
                <ProductActionButton>
                    <FitScreenIcon color="primary"/>
                </ProductActionButton>

            </Stack>
           </ProductActionsWrapper>
        </Product>
         </>
        

    )
}
