import { Container, Grid, useMediaQuery, useTheme } from "@mui/material";
import { products } from "../../data";
import SingleProducts from "./SingleProducts";
import SingleProductsDesktop from "./SingleProductsDesktop";

import {getProductsToDisplay} from '../productdetail'


export default function Products() {
  const theme = useTheme();
  const matches = useMediaQuery(theme.breakpoints.down("md"));
  const displayProducts = getProductsToDisplay(products, showAll);
  const renderPrdoducts = displayProducts.map((product) => (
    <Grid
      item
      key={product.id}
      xs={12}
      sm={6}
      md={4}
      
      display="flex"
      flexDirection={"column"}
      alignItems="center"
    >
   
      {matches ? (<SingleProducts product={product} matches={matches} />
      ) : ( <SingleProductsDesktop product={product} matches={matches}/>) }
      
    </Grid>
  ));
  return (
    <Container
      sx={{
        display: "flex",my: "20px", padding:0,
      }}
    >
      <Grid
        container
        spacing={{
          xs: 2,
          md: 3,
        }}
        justifyContent={"center"}
        alignItems={"center"}
        sx={{my: "20px", padding:0, }}
      >
        {renderPrdoducts}
      </Grid>
    </Container>
  );
}
