import { Button, Container, Grid, List, ListItemButton, ListItemIcon, ListItemText, Typography } from '@mui/material';
import React from 'react'
import {  ProductImage } from '../../styles/products';
import { products } from '../../data';
import { Matches } from '../../styles/theme';
import PageBanner from '../banner/PageBanner';
import RemoveIcon from '@mui/icons-material/Remove';
import AddIcon from '@mui/icons-material/Add';

const ProductDetail = () => {
    const matches= Matches();
    console.log(products[0].images);
    
  return (
    <>
    <Container>
     <PageBanner
    heading="Products Detail"
    breadcrumbLinks={[
      { label: 'Product', href: '/product' },
      { label: 'ProductDetails', href: '/productdetail' }
    ]}
  />
        <Grid container mt={3}>
            <Grid item md={6} sx={{textAlign:"center"}}> 
                <ProductImage src={products[0].images} sx={{
                    maxWidth:"inherit"
                }}/>
            </Grid>
            <Grid item md={6}>
                <Typography  variant={matches ? "h6" : "h5"} lineHeight={2}>
                    {products[0].name}
                </Typography>
            <Typography variant={matches ? "caption" : "body1"}>
               <b>Price:</b> ${products[0].price}
            </Typography>
            <Typography variant={ "body1"}>
                <b>Description:</b> {products[0].description}
            </Typography>
            <Button variant="contained">Add to cart</Button>
            <List sx={{
                display:"flex",
                alignItems:"center"

            }}>
                <ListItemButton sx={{
                        width:"50px",
                        padding:"10px",
                    }}>
                    <ListItemIcon><RemoveIcon/></ListItemIcon>
                </ListItemButton>
                <ListItemButton  sx={{
                        width:"80px",
                        padding:"10px ",
                    }}>
                    <ListItemText>Count</ListItemText></ListItemButton>
                <ListItemButton sx={{
                        width:"50px",
                        padding:"10px",
                    }}> <ListItemIcon><AddIcon/></ListItemIcon></ListItemButton>

            </List>
            </Grid>
        </Grid>
    </Container>
    </>
  )
}

export default ProductDetail;
