    "please look up to my Website :)",
];

export default function Promotions(){

    cosnt [messageIndex, setMessageIndex]= useState(0)

    return(
         <PromotionsContainer>
            <Slide direction="left">
            <Box>
                <MessageText>
                    {messages[messageIndex]}
